mapper.insert:
INSERT INTO EQM(
			EQM_CODE
			, EQM_NAME
			, MODEL_NAME
			, MAKER
			, PURPOSE
			, INSTALL_PLACE
			, BUY_COMPANY
			, BUY_PRICE
			, BUY_DATE
			, DUMP_DATE
			, CHECK_CYCLE
			, LAST_CHECK_DATE
			, EQM_STATUS
			, OPR_STATUS)
VALUES(
	seq_func('eqm')
	, #{eqmName}
	, #{modelName}
	, #{maker}
	, #{purpose}
	, #{installPlace}
	, #{buyCompany}
	, #{buyPrice}
	, #{buyDate}
	, #{dumpDate}
	, #{checkCycle}
	, #{lastCheckDate}
	, #{eqmStatus}
	, #{oprStatus})

mapper.update:
UPDATE EQM
SET EQM_NAME = #{eqmName}
	, MODEL_NAME = #{modelName}
	, MAKER = #{maker}
	, PURPOSE = #{purpose}
	, INSTALL_PLACE = #{installPlace}
	, BUY_COMPANY = #{buyCompany}
	, BUY_PRICE = #{buyPrice}
	, BUY_DATE = #{buyDate}
	, DUMP_DATE = #{dumpDate}
	, CHECK_CYCLE = #{checkCycle}
	, LAST_CHECK_DATE = #{lastCheckDate}
	, EQM_STATUS = #{eqmStatus}
	, OPR_STATUS = #{oprStatus}
WHERE EQM_CODE = #{eqmCode}
