mapper.select:
WITH t AS ( SELECT o.orders_code AS ocode , MAX(p.product_name) AS prod , COUNT(*) AS cnt FROM orders o JOIN orders_detail d ON o.orders_code = d.orders_code JOIN product p ON d.product_code = p.product_code GROUP BY o.orders_code ) SELECT o.orders_code , o.orders_date , o.due_date , o.total_orders_price , o.orders_status , c.company_name , u.users_name , CASE WHEN t.cnt = 1 THEN t.prod ELSE t.prod || ' 외 ' || CAST(t.cnt - 1 AS VARCHAR(20)) || '건' END AS cnt_str FROM orders o JOIN company c ON o.company_code = c.company_code JOIN users u ON o.users_code = u.users_code JOIN t ON o.orders_code = t.ocode WHERE o.orders_status = 'OP1'
AND o.orders_code LIKE '%' || #{ordersCode} || '%'
AND o.company_code = #{companyCode}
AND o.due_date >= #{dueStartDate}
AND o.due_date <= #{dueEndDate}
AND o.orders_date >= #{ordersStartDate}
AND o.orders_date <= #{ordersEndDate}
ORDER BY 1

SELECT p.prod_req_code , p.total_req_cnt , p.req_date , p.prod_req_status , u.users_name FROM prod_req p JOIN users u ON p.users_code = u.users_code WHERE 1=1
AND p.prod_req_code LIKE '%' || #{prodReqCode} || '%'
AND u.users_name LIKE '%' || #{usersName} || '%'
AND p.req_date >= #{prodReqStartDate}
AND p.req_date <= #{prodReqEndDate}
ORDER BY p.req_date DESC

mapper.insert:


 	SELECT seq_func('orders') FROM dual

INSERT INTO orders
	(
		orders_code
		, orders_date
		, due_date
		, total_orders_price
		, orders_status
		, users_code
		, company_code
	)
VALUES 
	(
		 #{ordersCode}
		, #{ordersDate}
		, #{dueDate}
		, #{totalOrdersPrice}
		, 'OP1'
		, 'USE00002'
		, #{companyCode}
	)


INSERT INTO orders_detail
	(
		orders_detail_code
		, orders_cnt
		, supply_price
		, tax
		, total_supply_price
		, orders_code
		, product_code
	)
VALUES
	(
		seq_func('orders_detail')
		, #{ordersCnt}
		, #{supplyPrice}
		, #{tax}
		, #{totalSupplyPrice}
		, #{ordersCode}
		, #{productCode}
	)

INSERT INTO prod_req_detail
	(
		prod_req_detail_code
		, req_cnt
		, prod_req_code
		, product_code
	)
VALUES
	(
		seq_func('prod_req_detail')
		, #{reqCnt}
		, #{prodReqCode}
		, #{productCode}
	)

mapper.update:
UPDATE orders
   SET orders_status = 'OP3'
 WHERE due_date  >=  #{dueStartDate}
   AND due_date  <=  #{dueEndDate}

mapper.delete:
DELETE FROM orders
 WHERE orders_code = #{ordersCode}
   AND orders_status = 'OP1'
