mapper.select:
WITH t AS ( SELECT d.product_code , SUM(d.orders_cnt) cnt FROM orders_detail d JOIN orders o ON o.orders_code = d.orders_code WHERE o.orders_status = 'OP2'
AND o.due_date >= #{dueStartDate}
AND o.due_date <= #{dueEndDate}
GROUP BY d.product_code ) SELECT p.product_code , p.product_name , p.unit_price , p.stock_cnt , p.safe_stock_cnt , NVL(t.cnt, '0') total_orders_cnt FROM t RIGHT OUTER JOIN product p ON t.product_code = p.product_code ORDER BY 1

mapper.insert:
INSERT INTO product_inout_history
    (
        product_inout_code
        , inout_sep
        , inout_cnt
        , inout_date
        , product_lot_code
        , orders_detail_code
        , users_code
    )
VALUES
    (
		seq_func(' product_inout_history')
		, 'IO2'
		, #{inoutCnt}
		, #{inoutDate}
		, #{productLotCode}
		, #{ordersDetailCode}
		, #{usersCode}
    )

mapper.update:
UPDATE orders
   SET orders_status = 'OP4'
 WHERE orders_code = #{ordersCode}
