mapper.insert:
INSERT INTO MAT_QLTY_CHECK
			(
			mat_qlty_check_code
			, mat_lot_code
			, check_recv_date
			, warehousing_vehicles_check
			, foreign_exist
			, pack_status
			, last_result
			, good_cnt
			, users_code
			)
	VALUES (
			seq_func('MAT_QLTY_CHECK')
			, #{matLotCode}
			, trunc(sysdate)
			, #{warehousingVehiclesCheck}
			, #{foreignExist}
			, #{packStatus}
			, #{lastResult}
			, #{goodCnt}
			, 'USE00005'
			)

mapper.update:
 UPDATE mat
	SET STOCK_CNT = STOCK_CNT + round(#{goodCnt}/30,1)
  WHERE mat_code = #{matCode}

UPDATE mat_detail
SET CHECK_DONE_CNT = #{goodCnt}
	, REMAIN_CNT =  round(#{goodCnt}/30,1)
	, QLTY_CHECK_STATUS = 'MI2'
WHERE MAT_LOT_CODE = #{matLotCode}

mapper.insert:
INSERT INTO PRODUCT_QLTY_CHECK
			(
			product_qlty_check_code
			, product_lot_code
			, check_recv_date
			, foreign_exist
			, pack_status
			, add_stand
			, product_weight
			, last_result
			, good_cnt
			)
	VALUES (
			seq_func('PRODUCT_QLTY_CHECK')
			, #{productLotCode}
			, trunc(sysdate)
			, #{foreignExist}
			, #{packStatus}
			, #{addStand}
			, #{productWeight}
			, #{lastResult}
			, #{goodCnt}
			)

mapper.update:
UPDATE product_detail
   SET check_done_cnt = #{goodCnt}
   		, remain_cnt = #{goodCnt}
   		, delivery_cnt = 0
   		, qlty_check_status = 'PQ2'
 WHERE PRODUCT_LOT_CODE = #{productLotCode}
